import React, { Component } from 'react'
import PropTypes from 'prop-types'
import { withStyles } from '@material-ui/core/styles'
import Grid from '@material-ui/core/Grid'
import Tabs from '@material-ui/core/Tabs'
import Tab from '@material-ui/core/Tab'
import phone from '../../img/phone-screen.png'

const styles = {
  root: {
    flexGrow: 1,
  },
  rootGrid: {
    alignContent: 'center',
    justify: 'center',
  }
}

class Features extends Component {
  state = {
    value: 0,
  }

  handleChange = (event, value) => {
    this.setState({ value })
  }

  render() {
    const { classes } = this.props

    return (
      <div classes={{root: classes.root}} className='wrapper'>
        <Tabs
          value={this.state.value}
          onChange={this.handleChange}
          indicatorColor="primary"
          textColor="primary"
          centered
        >
          <Tab label="For Clients" />
          <Tab label="For Techkies" />
        </Tabs>

        <Grid container spacing={24} className={classes.rootGrid}>
          <Grid item xs={12} md={4}>
            <img style={{width: 200}} src={phone} alt='Features for Clients' />
            <h4>Feature 1</h4>
          </Grid>
        </Grid>
      </div>
    )
  }
}

Features.propTypes = {
  classes: PropTypes.object.isRequired,
}

export default withStyles(styles)(Features)
