import React, { Component } from 'react'
import PropTypes from 'prop-types'
import { withStyles } from '@material-ui/core/styles'
import { Tab, Tabs } from '@material-ui/core'
import FeaturesClient from './FeaturesClient'
import FeaturesTechky from './FeaturesTechky'
// import white from '@material-ui/core/colors/white'

const styles = theme => ({
	root: {
		position: 'relative',
	},
	background:{ 
		position: 'absolute',
		top: 450,
		bottom: 0,
		left: 0,
		right: 0,
		zIndex: -1,
		[theme.breakpoints.up('md')]: {
			backgroundColor: theme.palette.primary.lightest,
		},
	},
	indicator: {
		background: theme.palette.secondary.main,
	},
})

class Features extends Component {
  state = {
    value: 'client',
  }

  handleChange = (event, value) => {
    this.setState({ value })
  }

  render() {
		const { classes } = this.props
		const indicator = {
			background: 'red',
		}

    return (
      <div className={classes.root}>
				<div className={classes.background}></div>
				<section className='wrapper' id='features'>
					<h2 className='center marginTop'>Features</h2>
					<Tabs
						value={this.state.value}
						onChange={this.handleChange}
						TabIndicatorProps={indicator}
						textColor="secondary"
						centered
					>
						<Tab label='For Clients' value='client' />
						<Tab label='For Techkies' value='techky' />
					</Tabs>

					{ this.state.value === 'client' ? <FeaturesClient /> : <FeaturesTechky /> }
				</section>

      </div>
    )
  }
}

Features.propTypes = {
  classes: PropTypes.object.isRequired,
}

export default withStyles(styles)(Features)
