import React, { Component } from 'react'
import PropTypes from 'prop-types'
import { withStyles } from '@material-ui/core/styles'
import { Tab, Tabs } from '@material-ui/core'
import FeaturesClient from './FeaturesClient'
import FeaturesTechky from './FeaturesTechky'

const styles = theme => ({
	root: {
		position: 'relative',
	},
	background:{ 
		position: 'absolute',
		top: 450,
		bottom: 0,
		left: 0,
		right: 0,
		zIndex: -1,
		[theme.breakpoints.up('md')]: {
			backgroundColor: theme.palette.primary.lightest,
		},
	},
	tabsRoot: {
		fontWeight: 600,
		color: theme.palette.common.white,
	},
	tabRootLeft: {
		background: theme.palette.primary.main,
		borderTopLeftRadius:10,
		borderBottomLeftRadius:10,
	},
	tabRootRight: {
		// fontWeight: 600,
		background: theme.palette.primary.main,
		borderTopRightRadius:10,
		borderBottomRightRadius:10,
		// color: theme.palette.common.white,
	},
	tabSelected: {
		backgroundColor: theme.palette.secondary.main
	}
})

class Features extends Component {
  state = {
    value: 'client',
  }

  handleChange = (event, value) => {
    this.setState({ value })
  }

  render() {
		const { classes } = this.props

    return (
      <div className={classes.root}>
				<div className={classes.background}></div>
				<section className='wrapper' id='features'>
					<h2 className='center marginTop'>Features</h2>
					<Tabs
						value={this.state.value}
						onChange={this.handleChange}
						classes={{root: classes.tabsRoot, indicator: classes.tabsIndicator }}
						centered
					>
						<Tab classes={{ root: classes.tabRootLeft, selected: classes.tabSelected }} label='For Clients' value='client' />
						<Tab classes={{ root: classes.tabRootRight, selected: classes.tabSelected }} label='For Techkies' value='techky' />
					</Tabs>

					{ this.state.value === 'client' ? <FeaturesClient /> : <FeaturesTechky /> }
				</section>

      </div>
    )
  }
}

Features.propTypes = {
  classes: PropTypes.object.isRequired,
}

export default withStyles(styles)(Features)
